import React from "react";
import VWorld from './Virtual World.png';
const projectItems = [
  {
      id:1,
      img:'./project.png',
      title:"Portfolio Site",
      description:'My own portfolio site created entirely using ReactJS and CSS. It displays my projects and my skills along with some other things about me. It also provides the user the facility to contact me through multiple platforms.(This site basically)'
  }, 
  {
      id:2,
      img:{VWorld},
      title:"Virtual World",
      description:'A virtual world interface created entirely with javascript with no pre-installed libraries used where the user can create road maps by choice and place markers on the roads.'
  },
  {
      id:3,
      img:'',
      title:"Daankaren",
      description:'A donation platform that helps the users to select campaigns of their choice and donate money or other goods as per their will.(This project is in progress)'
  }
]
const items = () => {
  return (
    <>
      {projectItems.map((project) => {
        const { id, img, title, description } = project;
        return (
          <div className="portfolio-items card card-two" key={id}>
            <div className="portfolio-img-wrapper">
              <img src={img} className="portfolio-img"></img>
            </div>

            <h3 className="portfolio-title">{title}</h3>
            <p className="portfolio-description">{description}</p>
            <button>
              <span>View Demo</span>
            </button>
          </div>
        );
      })}
    </>
  );
};

export default items;
