import React from 'react'
import './intro.css';
import {Link} from 'react-scroll';

function intro() {
  return (
    <div id='intro'>
    
        {/* <img src='https://i.pinimg.com/736x/dd/97/3a/dd973ac116a977c8dd5296b0da504b8c.jpg' alt='image' className='introImg'/> */}
            <video autoPlay className='introImg'>
          <source src="https://assets-global.website-files.com/5e18a1cc86ccdc5d5a0d353d/6436c09081e7055dd8e3e27c_Hero-2023-lg-transcode.mp4"></source>
        </video>
        <div className = 'introcontent'>
        <span className ='Hi'>Hi</span>
        <span className='introText'>I am<span className='introName'> Bhavya Arora</span><br></br>A Web Developer</span>
        <p className='introPara'>I am a student at National Institute of Technology Kurukshetra</p>
            <Link><button className='btn'><img src='https://icons.veryicon.com/png/o/miscellaneous/general-icon-library/resume-7.png' alt='btnImage' className='btnImg'/>My Resume</button></Link>
        </div>
    </div>
  )
}

export default intro;
