import React from 'react'
import './intro.css';
import {Link} from 'react-scroll';

function intro() {
  return (
    <section id='intro'>
        <div className = 'introcontent'>
        <span className ='Hi'>Hi</span>
        <span className='introText'>I am<span className='introName'> Bhavya Arora</span><br></br>Web Developer</span>
        <p className='introPara'>I am a student at National Institute of Technology Kurukshetra</p>
            <Link><button className='btn'>My Resume</button></Link>
        </div>
        <img src='https://people.com/thmb/h67yba4NaGbpnv9DZVZm894kSjo=/1500x0/filters:no_upscale():max_bytes(150000):strip_icc():focal(979x292:981x294)/Brad-Pitt_1-2000-e8a294b80b034e659785ad813d3f02f2.jpg' alt='image'/>
    </section>
  )
}

export default intro;
